mapper.select:
	select
	
	from vend_order
	
		
			and usercode = #{vendOrder.usercode}
		
		
			and shopusercode = #{vendOrder.shopusercode}
		
		
			and goods_id = #{vendOrder.goodsId}
		
		
			and machine_code = #{vendOrder.machineCode}
		
		
             and DATE_FORMAT(vendOrder.create_time, '%Y-%m-%d %H:%i:%S')>=  DATE_FORMAT(#{beginTime}, '%Y-%m-%d %H:%i:%S')   
        
        
             and DATE_FORMAT(vendOrder.create_time, '%Y-%m-%d %H:%i:%S')<=  DATE_FORMAT(#{endTime}, '%Y-%m-%d %H:%i:%S')   
        
	order by order_id limit #{page.dbIndex},#{page.dbNumber}

	select
	
	from vend_order
	
		
			and usercode = #{vendOrder.usercode}
		
		
			and shopusercode = #{vendOrder.shopusercode}
		
		
			and goods_id = #{vendOrder.goodsId}
		
		
			and machine_code = #{vendOrder.machineCode}
		
		
             and DATE_FORMAT(vendOrder.create_time, '%Y-%m-%d %H:%i:%S')>=  DATE_FORMAT(#{beginTime}, '%Y-%m-%d %H:%i:%S')   
        
        
             and DATE_FORMAT(vendOrder.create_time, '%Y-%m-%d %H:%i:%S')<=  DATE_FORMAT(#{endTime}, '%Y-%m-%d %H:%i:%S')

 	select count(*) from vend_order
 	
 		
			and usercode = #{vendOrder.usercode}
		
		
			and shopusercode = #{vendOrder.shopusercode}
		
		
			and goods_id = #{vendOrder.goodsId}
		
		
			and machine_code = #{vendOrder.machineCode}
		
		
             and DATE_FORMAT(vendOrder.create_time, '%Y-%m-%d %H:%i:%S')>=  DATE_FORMAT(#{beginTime}, '%Y-%m-%d %H:%i:%S')   
        
        
             and DATE_FORMAT(vendOrder.create_time, '%Y-%m-%d %H:%i:%S')<=  DATE_FORMAT(#{endTime}, '%Y-%m-%d %H:%i:%S')
